<script>
import SouthTyrolHeader from "../components/SouthTyrolHeader.vue";
import Service from "../service/index.js";
import {
  LMap,
  LIcon,
  LTileLayer,
  LMarker,
  LControlLayers,
  LTooltip,
} from "@vue-leaflet/vue-leaflet";
import "leaflet/dist/leaflet.css";

export default {
  name: "Hotel",
  components: {
    SouthTyrolHeader,
    LMap,
    LIcon,
    LTileLayer,
    LMarker,
    LControlLayers,
    LTooltip,
  },
  data() {
